import "/Users/pinkdove/RecessApplication/src/LoginApp.css";


class Recess2 extends Component {

  constructor(){
    super();
  this.state = {
    username: '',
    password: '',
  };

}

onChange = (e) =>{
this.setState({ [e.target.name]: e.target.value});
}

